public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(10<100);
		
		
		if (10>100) {
			
			System.out.println("condition was true");
		}else {
			System.out.println("condition was false");
		}
		
		
	if (10<100) {
			
			System.out.println("condition was true");
		}else {
			System.out.println("condition was false");
	}

	}
}
