package day_17;

public class WhileLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int n1= 100;
		int n2= 140;
		
		while (n2>n1) {
			System.out.println("N2 is greater");
			n1+=10;
		}
		
		System.out.println("END");
		
		
		int num = 0;
		while (num<5) {
			System.out.println("Java is fun");
			num++;
		}

		
	}

}
